mapper.delete:
DELETE FROM feed_coeat_request
WHERE feed_id = #{feedId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(1)
FROM feed_coeat_request
WHERE feed_id = #{feedId}
  AND user_id = #{userId}

mapper.update:
UPDATE feed_coeat_request
SET status = #{status}
WHERE feed_id = #{feedId}
  AND user_id = #{userId}
