mapper.delete:
DELETE FROM follow
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}

mapper.select:
SELECT COUNT(1)
FROM follow
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}

mapper.update:
UPDATE follow
SET notified = #{notified}
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}
